
    tinymce.baseURL = '/tinymce/';
    tinymce.init({
      menubar: false,
      selector:'textarea.richTextBox',
      skin_url: $('meta[name="assets-path"]').attr('content')+'?path=js/skins/voyager',
      min_height: 350,
      resize: 'vertical',
      plugins: 'link, image imagetools, code, youtube, giphy, table, textcolor, lists,powerpaste,directionality',
      extended_valid_elements : 'input[id|name|value|type|class|style|required|placeholder|autocomplete|onclick]',
      file_browser_callback: function(field_name, url, type, win) {
              if(type =='image'){
                $('#upload_file').trigger('click');
              }
          },
      toolbar: 'styleselect bold italic underline fontsizeselect | paste pastetext |  forecolor backcolor | alignleft aligncenter alignright | bullist numlist outdent indent ltr rtl | link image table youtube giphy | code',
      fontsize_formats: "8pt 10pt 12pt 14pt 18pt 24pt 36pt",
      paste_enable_default_filters: false,
      convert_urls: false,
      image_caption: true,
      image_title: true,
      imagetools_toolbar: "rotateleft rotateright | flipv fliph | editimage imageoptions",
      directionality :"rtl",
      powerpaste_allow_local_images: true,
        powerpaste_word_import: 'prompt',
        powerpaste_html_import: 'prompt',
    });

